FROM (
  SELECT MIN(heart_rate) AS min_hr
  FROM monitoring.monitoring_hr
  WHERE timestamp >= date('now', '-7 days')
    AND strftime('%H', timestamp) BETWEEN '05' AND '08'
  GROUP BY date(timestamp)
);

