SELECT ROUND(
  AVG(
    (strftime('%s', '1970-01-01 ' || deep_sleep) - strftime('%s', '1970-01-01')) * 100.0 /
    NULLIF(strftime('%s', '1970-01-01 ' || total_sleep) - strftime('%s', '1970-01-01'), 0)
  ), 0) AS value
FROM garmin.sleep
WHERE day >= date('now', '-7 days')
  AND total_sleep IS NOT NULL
  AND total_sleep != '00:00:00.000000';

